import React, { useState, useEffect } from "react";
import { StyleSheet, Text, View, TouchableOpacity, Image } from "react-native";
import { CollectionDrawing } from "../../components/CollectionDrawing";
import { useSelector, useDispatch } from "react-redux";
import { auth, firestore } from "../../firebase/config";

export const ProfileScreen = () => {
  const dispatch = useDispatch();
  const [currentUserPost, setcurrentUserPost] = useState([]);

  const { userId, userName, avatar } = useSelector((state) => state.user);

  useEffect(() => {
    getCurrentUserPosts();
  }, [userId]);

  const getCurrentUserPosts = async () => {
    await firestore
      .collection("posts")
      .where("userId", "==", userId)
      .onSnapshot((data) =>
        setcurrentUserPost(data.docs.map((doc) => doc.data()))
      );
  };

  const signOut = async () => {
    await auth.signOut();
    dispatch({ type: "USER_SIGNOUT" });
  };

  return (
    <View style={styles.container}>
      <View style={{ ...StyleSheet.absoluteFill }}>
        <Image
          source={require("../../assets/images/road.jpg")}
          style={{ flex: 1, width: null, height: null }}
        />
      </View>
      <TouchableOpacity style={styles.btn} onPress={signOut}>
        <Text style={styles.btnTitle}>SignOut {userName}</Text>
      </TouchableOpacity>
      <Image
            style={{
              width: 60,
              height: 60,
              borderRadius: 10,
              margin:10,
            }}
            source={{
              uri:avatar,
            }}
          />
      <View style={{ flex:1,justifyContent:"center" }}>
        <Text style={styles.myPostTitle}>{userName} posts</Text>
        <CollectionDrawing data={currentUserPost} />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
  btn: {
    height: "auto",
    width: "auto",
    backgroundColor: "#556b2f",
    padding: 10,
    borderRadius: 18,
    marginTop: 40,
    justifyContent: "center",
    alignItems: "center",
  },
  btnTitle: {
    color: "#fff",
    fontSize: 18,
  },
  myPostTitle: {
    fontSize: 18,
    color:"#fff",
    textAlign: "center",
    backgroundColor: "#556b2f",
    margin: 10,
    padding: 10,
    borderRadius: 20,
  },
});
